    private void btnGuardarActionPerformed(java.awt.event.ActionEvent evt) {                                           
         
        try {
            Conexion cc = new Conexion();
            Connection cn = cc.conectar();
            
            PreparedStatement ps = cn.prepareStatement("INSERT INTO Juegos VALUES(?,?,?,?,?,?,?,?,?,?,?,?);");
            
            int codigo = Integer.parseInt(txtCodigo.getText());
            String nombre = txtNombre.getText();
            int precio = Integer.parseInt(txtPrecio.getText());
            int cantidad = Integer.parseInt(txtCantidad.getText());
            String tiempo = txtTiempo.getText();
            int edad = Integer.parseInt(txtEdad.getText());
            String descripcion = txtDescripcion.getText();
            String idioma = txtIdioma.getText();
            String editor = txtEditor.getText();
            String disenador = txtDisenador.getText();
            String artista = txtArtista.getText();
            int stock = Integer.parseInt(txtStock.getText());
            
            ps.setInt(1, codigo);
            ps.setString(2, nombre);
            ps.setInt(3, precio);
            ps.setString(4, descripcion);
            ps.setInt(5, cantidad);
            ps.setString(6, tiempo);
            ps.setInt(7, edad);
            ps.setString(8, idioma);
            ps.setString(9, editor);
            ps.setString(10, disenador);
            ps.setString(11, artista);
            ps.setInt(12, stock);
            
            ps.executeUpdate();
            limpiarForm();
            CargarData();
            JOptionPane.showMessageDialog(null,"¡Juego Guardado Correctamente!");
        } 
        
        catch (Exception e){
            System.out.println(e.getMessage());
        }   
        
    }                                          
    
