    private void TablaMouseClicked(java.awt.event.MouseEvent evt) {                                   
        
        int fila = Tabla.getSelectedRow();
        
        txtCodigo.setText(Tabla.getValueAt(fila, 0).toString());
        txtNombre.setText(Tabla.getValueAt(fila, 1).toString());
        txtPrecio.setText(Tabla.getValueAt(fila, 2).toString());
        txtStock.setText(Tabla.getValueAt(fila, 3).toString());
        txtCantidad.setText(Tabla.getValueAt(fila, 4).toString());
        txtTiempo.setText(Tabla.getValueAt(fila, 5).toString());
        txtEdad.setText(Tabla.getValueAt(fila, 6).toString());
        txtIdioma.setText(Tabla.getValueAt(fila, 7).toString());
        txtEditor.setText(Tabla.getValueAt(fila, 8).toString());
        txtDisenador.setText(Tabla.getValueAt(fila, 9).toString());
        txtArtista.setText(Tabla.getValueAt(fila, 10).toString());
        txtDescripcion.setText(Tabla.getValueAt(fila, 11).toString());
        
    }                                  

    private void btnEliminarActionPerformed(java.awt.event.ActionEvent evt) {                                            
       try {
            Conexion cc = new Conexion();
            Connection cn = cc.conectar();
            
            PreparedStatement ps = cn.prepareStatement("DELETE FROM Juegos WHERE id = ?;");
            
            int codigo = Integer.parseInt(txtCodigo.getText());
            
            ps.setInt(1, codigo);
       
            ps.executeUpdate();
            limpiarForm();
            CargarData();
            JOptionPane.showMessageDialog(null,"¡Juego Eliminado Correctamente!");
        } 
        
        catch (Exception e){
            System.out.println(e.getMessage());
        }   
    }                                           

    private void btnEditarActionPerformed(java.awt.event.ActionEvent evt) {                                          
        try {
            Conexion cc = new Conexion();
            Connection cn = cc.conectar();
            
            PreparedStatement ps = cn.prepareStatement("UPDATE Juegos SET nombre=?, precio=?, "
                    + "descripcion=?, cantidad_jugadores=?, tiempo_estimado_por_partida=?, edad_recomendada=?, "
                    + "idioma=?, editor=?, disenador=?, artista=?, stock=? WHERE id=?;");
            
            int codigo = Integer.parseInt(txtCodigo.getText());
            String nombre = txtNombre.getText();
            int precio = Integer.parseInt(txtPrecio.getText());
            int cantidad = Integer.parseInt(txtCantidad.getText());
            String tiempo = txtTiempo.getText();
            int edad = Integer.parseInt(txtEdad.getText());
            String descripcion = txtDescripcion.getText();
            String idioma = txtIdioma.getText();
            String editor = txtEditor.getText();
            String disenador = txtDisenador.getText();
            String artista = txtArtista.getText();
            int stock = Integer.parseInt(txtStock.getText());
            
            
            ps.setString(1, nombre);
            ps.setInt(2, precio);
            ps.setString(3, descripcion);
            ps.setInt(4, cantidad);
            ps.setString(5, tiempo);
            ps.setInt(6, edad);
            ps.setString(7, idioma);
            ps.setString(8, editor);
            ps.setString(9, disenador);
            ps.setString(10, artista);
            ps.setInt(11, stock);
            ps.setInt(12, codigo);
            
            ps.executeUpdate();
            limpiarForm();
            CargarData();
            JOptionPane.showMessageDialog(null,"¡Juego Actualizado Correctamente!");
        } 
        
        catch (Exception e){
            System.out.println(e.getMessage());
        }
    }                                         
    
    public void CargarData(){
        try {
           
           DefaultTableModel modelo = new DefaultTableModel();
           
           modelo.addColumn("Código");
           modelo.addColumn("Nombre");
           modelo.addColumn("Precio");
           modelo.addColumn("Stock");
           modelo.addColumn("Catidad Jugadores");
           modelo.addColumn("Tiempo");
           modelo.addColumn("Edad");
           modelo.addColumn("Idioma");
           modelo.addColumn("Editor");
           modelo.addColumn("Diseñador");
           modelo.addColumn("Artistas");
           modelo.addColumn("Descripción");
           
            Conexion cc = new Conexion();
            Connection cn = cc.conectar();
            
            Statement st = cn.createStatement();
            
            ResultSet rs = st.executeQuery("SELECT * FROM Juegos");
            
            while (rs.next()){
               String [] fila = new String[12];
               fila[0] = rs.getString(1);
               fila[1] = rs.getString(2);
               fila[2] = rs.getString(3);
               fila[3] = rs.getString(12);
               fila[4] = rs.getString(5);
               fila[5] = rs.getString(6);
               fila[6] = rs.getString(7);
               fila[7] = rs.getString(8);
               fila[8] = rs.getString(9);
               fila[9] = rs.getString(10);
               fila[10] = rs.getString(11);
               fila[11] = rs.getString(4);
               
               modelo.addRow(fila);
               
            }
            
            Tabla.setModel(modelo);
            
        } 
        
        catch (Exception e){
            System.out.println(e.getMessage());
        }
    } 
    
    
